pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./UtilitiesUpgradeable.sol";

// Do not add state to this contract.
//
contract AdminableUpgradeable is UtilitiesUpgradeable {

    mapping(address => bool) private admins;

    function __Adminable_init() internal initializer {
        UtilitiesUpgradeable.__Utilities__init();
    }

    function addAdmin(address _address) external onlyOwner {
        admins[_address] = true;
    }

    function addAdmins(address[] calldata _addresses) external onlyOwner {
        for(uint256 i = 0; i < _addresses.length; i++) {
            admins[_addresses[i]] = true;
        }
    }

    function removeAdmin(address _address) external onlyOwner {
        admins[_address] = false;
    }

    function removeAdmins(address[] calldata _addresses) external onlyOwner {
        for(uint256 i = 0; i < _addresses.length; i++) {
            admins[_addresses[i]] = false;
        }
    }

    function setPause(bool _shouldPause) external onlyAdminOrOwner {
        if(_shouldPause) {
            _pause();
        } else {
            _unpause();
        }
    }

    function isAdmin(address _address) public view returns(bool) {
        return admins[_address];
    }

    modifier onlyAdmin() {
        require(admins[msg.sender], "Not admin");
        _;
    }

    modifier onlyAdminOrOwner() {
        require(admins[msg.sender] || isOwner(), "Not admin or owner");
        _;
    }
    uint256[50] private __gap;
}","keccak256":"0xb9c7084cdb7d0cc6d4f3e7ce6e68e5f75c0a99bf311b1975ed27d8ff3088c7b6","license":"MIT"},"contracts/shared/UtilitiesUpgradeable.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract UtilitiesUpgradeable is Initializable, OwnableUpgradeable, PausableUpgradeable {

    function __Utilities__init() internal initializer {
        OwnableUpgradeable.__Ownable_init();
        PausableUpgradeable.__Pausable_init();

        _pause();
    }

    modifier nonZeroAddress(address _address) {
        require(address(0) != _address, "0 address");
        _;
    }

    modifier nonZeroLength(uint[] memory _array) {
        require(_array.length > 0, "Empty array");
        _;
    }

    modifier lengthsAreEqual(uint[] memory _array1, uint[] memory _array2) {
        require(_array1.length == _array2.length, "Unequal lengths");
        _;
    }

    modifier onlyEOA() {
        /* solhint-disable avoid-tx-origin */
        require(msg.sender == tx.origin, "No contracts");
        _;
    }

    function isOwner() internal view returns(bool) {
        return owner() == msg.sender;
    }
}","keccak256":"0x6d8894c4823d477c71487e9d325ab3d7a595723868621f522b664f717341769b","license":"MIT"},"contracts/shared/randomizercl/IRandomizerCL.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IRandomizerCL {
    // Returns a request ID for the random number. This should be kept and mapped to whatever the contract
    // is tracking randoms for.
    // Admin only.
    function getRandomNumber() external returns(bytes32);

    // Returns the random for the given request ID.
    // Will revert if the random is not ready.
    function randomForRequestID(bytes32 _requestID) external view returns(uint256);

    // Returns if the request ID has been fulfilled yet.
    function isRequestIDFulfilled(bytes32 _requestID) external view returns(bool);
}","keccak256":"0x0543974321e677f332060f68731869f3904fb63c2a430945905ce639a441aca7","license":"MIT"},"contracts/wnd/rift/IRift.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../shared/IDragonStakable.sol";

interface IRift is IDragonStakable {

    // Returns the rift tier for the given user based on how much GP is staked at the rift.
    function getRiftTier(address _address) external view returns(uint256);
}","keccak256":"0xda50f9362b0d1afec30ad7082dc3f805e6712e561d9b97f902f5c81debf66828","license":"MIT"},"contracts/wnd/rift/Rift.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./RiftDragonStakable.sol";
import "./RiftContracts.sol";

contract Rift is Initializable, RiftDragonStakable {

    function initialize() public initializer {
        RiftDragonStakable.__RiftDragonStakable_init();
    }

    function setWizardSettings(uint8 _chanceWizardStolen, uint256 _treasureChestId) external onlyAdminOrOwner {
        require(_chanceWizardStolen >= 8 && _chanceWizardStolen <= 100, "Bad wizard chance");
        chanceWizardStolen = _chanceWizardStolen;
        treasureChestId = _treasureChestId;
    }

    // Moves tokens from L2 -> L1. L2 tokens are burnt.
    function transferToL1(
        uint256 _gpAmount,
        uint256[] calldata _wndTokenIds,
        uint256[] calldata _saIds,
        uint256[] calldata _saAmounts,
        uint256[] memory _consumablesIds,
        uint256[] memory _consumablesAmounts)
    external
    whenNotPaused
    contractsAreSet
    {
        require(_wndTokenIds.length <= 10, "too many NFTs to transfer");
        require(_gpAmount > 0 || _wndTokenIds.length > 0 || _saIds.length > 0 || _consumablesIds.length > 0, "Must select something to send");
        require(_saIds.length == _saAmounts.length, "Bad SA lengths");
        require(_consumablesIds.length == _consumablesAmounts.length, "Bad lengths");

        require(amountCurrentlyStaked >= amountNeededToOpenPortal, "Not enough GP staked");

        bytes memory _message = _getMessageForChild(_gpAmount, _wndTokenIds, _saIds, _saAmounts, _consumablesIds, _consumablesAmounts);

        childTunnel.sendMessageToRoot(_message);
    }

    // Handles validation logic and actually transfering tokens to this contract.
    function _getMessageForChild(
        uint256 _gpAmount,
        uint256[] memory _wndTokenIds,
        uint256[] memory _saIds,
        uint256[] memory _saAmounts,
        uint256[] memory _consumablesIds,
        uint256[] memory _consumablesAmounts)
        internal
        returns(bytes memory)
    {

        if(_gpAmount > 0) {
            // Burn GP
            gp.burn(msg.sender, _gpAmount);
        }

        // Transfer WnD
        for(uint256 i = 0; i < _wndTokenIds.length; i++) {
            uint256 _id = _wndTokenIds[i];
            require(_id != 0, "Bad Wnd ID");
            // Transfer to hold in this contract.
            wnd.adminTransferFrom(msg.sender, address(this), _id);
        }

        if(_saIds.length > 0) {
            sacrificialAlter.adminSafeBatchTransferFrom(msg.sender, address(this), _saIds, _saAmounts);
        }
        if(_consumablesIds.length > 0) {
            consumables.adminSafeBatchTransferFrom(msg.sender, address(this), _consumablesIds, _consumablesAmounts);
        }

        return abi.encode(msg.sender, _gpAmount, _wndTokenIds, _saIds, _saAmounts, _consumablesIds, _consumablesAmounts);
    }

    function handleMessage(bytes calldata _data) external override onlyAdminOrOwner {
        (address _to,
        uint256 _gpAmount,
        uint256[] memory _wndTokenIds,
        WizardDragon[] memory _traits,
        uint256[] memory _saIds,
        uint256[] memory _saAmounts,
        uint256[] memory _consumableIds,
        uint256[] memory _consumableAmounts) = abi.decode(
            _data,
            (address, uint256, uint256[], WizardDragon[], uint256[], uint256[], uint256[], uint256[])
        );

        processMessageFromRoot(_to, _gpAmount, _wndTokenIds, _traits, _saIds, _saAmounts, _consumableIds, _consumableAmounts);
    }

    // Handles a messagae from the root and creates/transfers tokens from this contract to the user in L2.
    function processMessageFromRoot(
        address _to,
        uint256 _gpAmount,
        uint256[] memory _wndTokenIds,
        WizardDragon[] memory _traits,
        uint256[] memory _saIds,
        uint256[] memory _saAmounts,
        uint256[] memory _consumableIds,
        uint256[] memory _consumableAmounts)
    public
    onlyAdminOrOwner
    {

        require(_saIds.length == _saAmounts.length, "Bad SA Amounts");
        require(_consumableIds.length == _consumableAmounts.length, "Bad Consumable Amounts");
        require(_wndTokenIds.length == _traits.length, "Bad WnD lengths");

        if(_gpAmount > 0) {
            gp.mint(_to, _gpAmount);
        }

        for(uint256 i = 0; i < _wndTokenIds.length; i++) {
            uint256 _tokenId = _wndTokenIds[i];
            require(_tokenId != 0, "Bad token id");

            _createWnDIfNeeded(_tokenId, _traits[i]);
        }

        if(_saIds.length > 0) {
            address[] memory _addresses = new address[](_saIds.length);
            for(uint256 i = 0; i < _saIds.length; i++) {
                _addresses[i] = address(this);
            }

            uint256[] memory _balances = sacrificialAlter.balanceOfBatch(_addresses, _saIds);

            for(uint256 i = 0; i < _saIds.length; i++) {
                uint256 _currentBalance = _balances[i];
                // They are requesting more than is held by this contract.
                //
                if(_currentBalance < _saAmounts[i]) {
                    sacrificialAlter.mint(_saIds[i], uint16(_saAmounts[i] - _currentBalance), address(this));
                }
            }

            sacrificialAlter.adminSafeBatchTransferFrom(address(this), _to, _saIds, _saAmounts);
        }

        if(_consumableIds.length > 0) {
            address[] memory _addresses = new address[](_consumableIds.length);
            for(uint256 i = 0; i < _consumableIds.length; i++) {
                _addresses[i] = address(this);
            }

            uint256[] memory _balances = consumables.balanceOfBatch(_addresses, _consumableIds);

            for(uint256 i = 0; i < _consumableIds.length; i++) {
                uint256 _currentBalance = _balances[i];
                // They are requesting more than is held by this contract.
                //
                if(_currentBalance < _consumableAmounts[i]) {
                    consumables.mint(_consumableIds[i], uint16(_consumableAmounts[i] - _currentBalance), address(this));
                }
            }

            consumables.adminSafeBatchTransferFrom(address(this), _to, _consumableIds, _consumableAmounts);
        }

        if(_wndTokenIds.length > 0) {
            _wizardAndDragonsReceivedFromL1(_to, _wndTokenIds);
        }
    }

    function _createWnDIfNeeded(uint256 _tokenId, WizardDragon memory _trait) private {
        if(wnd.exists(_tokenId)) {
            return;
        }

        wnd.mint(address(this), _tokenId, _trait);
    }

    // Called when a wizard or dragon is received from L1. At this point, the Wizards or
    // Dragons exist in L2 and are owned by this contract. Returns a request ID that will
    // be used to withdraw the wizard/dragons.
    function _wizardAndDragonsReceivedFromL1(
        address _recipient,
        uint256[] memory _wndTokenIds)
        private
    {
        uint256[] memory _wizardIds = new uint256[](_wndTokenIds.length);
        uint256 _wizardIndex = 0;
        for(uint256 i = 0; i < _wndTokenIds.length; i++) {
            uint256 _id = _wndTokenIds[i];
            if(_shouldKeepInRift(_id)) {
                _wizardIds[_wizardIndex] = _id;
                _wizardIndex++;
            } else {
                wnd.adminTransferFrom(address(this), _recipient, _id);
                emit TokenWithdrawn(_recipient, _id);
            }
        }

        if(_wizardIndex == 0) {
            // No wizards, so no need to get a random number.
            return;
        }

        bytes32 _requestID = randomizer.getRandomNumber();
        requestIdToTokens[_requestID] = _wizardIds;
        recipientToRequestIDs[_recipient].push(_requestID);
    }

    function withdrawWizardsToWallet() external contractsAreSet whenNotPaused onlyEOA {
        require(treasureChestId > 0, "Treasure chest ID not set");

        bytes32[] memory _requestIds = recipientToRequestIDs[msg.sender];
        require(_requestIds.length > 0, "No requests found");

        uint256 numWizardsHandled;
        for(uint256 i = 0; i < _requestIds.length; i++) {
            bytes32 _requestId = _requestIds[i];
            // _requestId will be 0 if partial wizards were withdrawn
            if(_requestId == 0) {
                continue;
            }
            require(randomizer.isRequestIDFulfilled(_requestId), "Request in progress");

            uint256 _randomness = randomizer.randomForRequestID(_requestId);
            uint256[] memory _tokenIds = requestIdToTokens[_requestId];
            for(uint256 j = 0; j < _tokenIds.length; j++) {
                uint256 _tokenId = _tokenIds[j];
                if(_tokenId == 0) {
                    continue;
                }

                // Different random per token.
                uint256 _randomForToken = uint256(keccak256(abi.encode(_randomness, _tokenId)));
                _decideFateOfWizard(_tokenId, _randomForToken);
                numWizardsHandled += 1;
            }

            // delete the value of the processed request in the array incase the entire queue of requests isn't complete in 1 tx
            delete recipientToRequestIDs[msg.sender][i];
            delete requestIdToTokens[_requestId];
            if(numWizardsHandled >= 50) {
                return;
            }
        }

        // Entire queue processed, delete the entire array to minimize the array size for next time
        delete recipientToRequestIDs[msg.sender];
    }

    function getPendingWizards(address _addr) external view returns(uint256[] memory) {
        uint256 numWizards;

        bytes32[] memory _requestIds = recipientToRequestIDs[_addr];

        for(uint256 i = 0; i < _requestIds.length; i++) {
            bytes32 _requestId = _requestIds[i];
            uint256[] memory _tokenIds = requestIdToTokens[_requestId];
            for(uint256 j = 0; j < _tokenIds.length; j++) {
                numWizards += 1;
            }
        }

        uint256[] memory _retVal = new uint256[](numWizards);
        uint256 _retIdx;

        for(uint256 i = 0; i < _requestIds.length; i++) {
            bytes32 _requestId = _requestIds[i];
            uint256[] memory _tokenIds = requestIdToTokens[_requestId];
            for(uint256 j = 0; j < _tokenIds.length; j++) {
                _retVal[_retIdx++] = _tokenIds[j];
            }
        }

        return _retVal;
    }

    function _shouldKeepInRift(uint256 _tokenId) private view returns(bool) {
        // The first 15000 wizards are safe from any chance of being stolen. Gen0.
        // All dragons are also safe from stealing
        if(_tokenId <= 15000) {
            return false;
        }
        return wnd.isWizard(_tokenId) && chanceWizardStolen != 0;
    }

    function _decideFateOfWizard(uint256 _tokenId, uint256 _random) private {
        uint256 _result = _random % 100;
        uint8 _wizardProf = trainingProficiency.proficiencyForWizard(_tokenId);
        uint8 _reduction = _wizardProf > 8 ? 8 : _wizardProf;

        uint256 _chanceWizardStolen = chanceWizardStolen - _reduction;

        bool _didWizardGetStolen = chanceWizardStolen != 0
            && world.totalNumberOfDragons() != 0
            && _result < _chanceWizardStolen;

        if(_didWizardGetStolen) {
            uint256 _newRandom = uint256(keccak256(abi.encode(_random, _random)));

            address _randomDragonOwner = world.getRandomDragonOwner(_newRandom, Location.RIFT);

            bool _hasChest = sacrificialAlter.balanceOf(msg.sender, treasureChestId) > 0;

            // Just look at the top bit for the 50/50 odds of stealing the chest
            if(_hasChest && _random >> 255 == 1) {
                sacrificialAlter.adminSafeTransferFrom(msg.sender, _randomDragonOwner, treasureChestId, 1);

                emit ChestStolen(msg.sender, _randomDragonOwner, _tokenId);

                wnd.adminTransferFrom(address(this), msg.sender, _tokenId);
                emit TokenWithdrawn(msg.sender, _tokenId);
            } else {
                wnd.adminTransferFrom(address(this), _randomDragonOwner, _tokenId);

                emit WizardStolen(msg.sender, _randomDragonOwner, _tokenId);
            }
        } else {
            // User keeps token.
            wnd.adminTransferFrom(address(this), msg.sender, _tokenId);
            emit TokenWithdrawn(msg.sender, _tokenId);
        }
    }

}","keccak256":"0xb0c83306d4da61f3394068d4bd2b6166084455ace998b5fcd7747dfd1f6af53e","license":"MIT"},"contracts/wnd/rift/RiftContracts.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./IRift.sol";
import "./RiftTier.sol";
import "./RiftState.sol";

abstract contract RiftContracts is Initializable, IRift, RiftTier {

    function __RiftContracts_init() internal initializer {
        RiftTier.__RiftTier_init();
    }

    function setContracts(
        address _worldAddress,
        address _wndAddress,
        address _trainingProficiencyAddress,
        address _randomizerAddress,
        address _childTunnelAddress,
        address _gpAddress,
        address _sacrificialAlterAddress,
        address _consumablesAddress)
    external
    onlyAdminOrOwner
    {
        world = IWorld(_worldAddress);
        wnd = IWnD(_wndAddress);
        trainingProficiency = ITrainingProficiency(_trainingProficiencyAddress);
        randomizer = IRandomizerCL(_randomizerAddress);
        childTunnel = IChildTunnel(_childTunnelAddress);
        gp = IGP(_gpAddress);
        sacrificialAlter = ISacrificialAlter(_sacrificialAlterAddress);
        consumables = IConsumables(_consumablesAddress);
    }

    modifier contractsAreSet() {
        require(areContractsSet(), "Rift: Contracts not set");

        _;
    }

    function areContractsSet() public view returns(bool) {
        return address(world) != address(0)
            && address(trainingProficiency) != address(0)
            && address(randomizer) != address(0)
            && address(wnd) != address(0)
            && address(gp) != address(0)
            && address(sacrificialAlter) != address(0)
            && address(consumables) != address(0)
            && address(childTunnel) != address(0);
    }
}","keccak256":"0x0bba5680262abd5f2d49d9f162cbf78c2970e36918bb1ab8b3a76ae58bacf8cc","license":"MIT"},"contracts/wnd/rift/RiftDragonStakable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./RiftContracts.sol";

abstract contract RiftDragonStakable is Initializable, RiftContracts {

    function __RiftDragonStakable_init() internal initializer {
        RiftContracts.__RiftContracts_init();
    }

    function stake(uint256 _tokenId, address _owner) external override onlyAdminOrOwner contractsAreSet {
        // Nothing needed except for adding the dragon to the world at the right location.
        // This will also transfer the token.
        world.addDragonToWorld(_tokenId, _owner, Location.RIFT);
    }

    function unstake(uint256 _tokenId, bool doTransfer) external override onlyAdminOrOwner contractsAreSet {
        // Nothing needed except for removing the dragon from the world and transferring it to the existing owner.
        // Adding the if statement in case future features are added that would change state regardless
        // of if a dragon is changing locations or going back to owner's wallet
        if(doTransfer) {
            address _owner = world.ownerOfTokenId(_tokenId);
            world.removeDragonFromWorld(_tokenId, _owner);
        }
    }
}","keccak256":"0xf73025019b837ed91a5762949aeb78bf0c4a876c488d51cb0cbadb999c3ae69b","license":"MIT"},"contracts/wnd/rift/RiftGP.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./RiftState.sol";

abstract contract RiftGP is Initializable, RiftState {

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    function __RiftGP_init() internal initializer {
        RiftState.__RiftState_init();
    }

    function updateStakeSettings(uint256 _amountNeededToOpenPortal) external onlyAdminOrOwner {
        amountNeededToOpenPortal = _amountNeededToOpenPortal;
    }

    function stakeGP(uint256 _amount) external whenNotPaused {
        require(_amount > 0, "Must stake some GP.");
        amountCurrentlyStaked += _amount;
        addressToGPStaked[msg.sender] += _amount;

        if(!addressesStaked.contains(msg.sender)) {
            addressesStaked.add(msg.sender);
        }

        bool _wasTransferred = gp.transferFrom(msg.sender, address(this), _amount);
        require(_wasTransferred, "GP not transferred");
    }

    function unstakeGP(uint256 _amount) external whenNotPaused {
        uint256 _amountStaked = addressToGPStaked[msg.sender];
        require(_amountStaked >= _amount, "Too much GP to unstake");

        amountCurrentlyStaked -= _amount;
        addressToGPStaked[msg.sender] -= _amount;

        if(addressToGPStaked[msg.sender] == 0) {
            addressesStaked.remove(msg.sender);
        }

        bool _wasTransferred = gp.transfer(msg.sender, _amount);
        require(_wasTransferred, "GP not transferred");
    }

}","keccak256":"0x4a17bd219e5d220e2eb7bd1a329241e7303508b4ad286c01623445503a6eb649","license":"MIT"},"contracts/wnd/rift/RiftState.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/utils/ERC721HolderUpgradeable.sol";
